int sol(int n,int m)
{
    if(n==0||m==0)  return 0;
    if(n==m)  return n;
    if(n<m)  swap(n,m);
    return m*(n/m)+sol(m,n%m);

}
int main()
{
    int n,m;
    cin>>n>>m;
    cout<<sol(n,m);
    return 0;
}
