    sol(2);

    int ANS=1e9;
    for(int i=1;i<top;++i)
      ANS=min(ANS,max(d[q[i+1]]-d[q[i]]+ans[2][i].len_R+ans[1][top-i].len_R,max(ans[2][i].len_L,ans[1][top-i].len_L)));
    cout<<ANS;
    return 0;
}
